mapper.insert:

INSERT INTO KPXERP_VDAMobile_ISU
	(	
		ENTER_CD
	  ,	SABUN
	  , ACCESS_TOKEN
	  , UPDATE_DATE
	  ,	CREATE_DATE
	  , NOTE
	)
	VALUES
	(	
		#{enterCd}
	  ,	#{sabun}
	  , #{accessToken}
	  , CURRENT_TIMESTAMP
	  ,	CURRENT_TIMESTAMP
	  , #{note}
	)

mapper.select:

SELECT ENTER_CD
	  ,	SABUN
	  , ACCESS_TOKEN
	FROM KPXERP_VDAMobile_ISU
	WHERE ENTER_CD = #{enterCd}
	AND SABUN = #{sabun}
	AND ACCESS_TOKEN = #{accessToken}

mapper.update:

UPDATE KPXERP_VDAMobile_ISU
		SET ACCESS_TOKEN = #{newAccessToken}
		,UPDATE_DATE = CURRENT_TIMESTAMP 
	WHERE ENTER_CD = #{enterCd}
	AND SABUN = #{sabun}
	AND ACCESS_TOKEN = #{accessToken}
