mapper.select:

SELECT SUM(CASE WHEN CASE X.HOL_EXCEPT_YN WHEN 'Y' THEN X.SUN_YN ELSE X.HOLIDAY_YN END = 'Y' THEN 0 ELSE 1 END) AS workCnt
FROM (
	SELECT D.HOLIDAY_YN, CASE WHEN H.SUN_YN IS NULL THEN 'N' ELSE H.SUN_YN END AS SUN_YN, M.HOL_EXCEPT_YN
	FROM WTM_FLEXIBLE_STD_MGR M
	JOIN WTM_DAY_MGR D
	ON D.SUN_YMD BETWEEN M.USE_SYMD AND M.USE_EYMD
	LEFT OUTER JOIN WTM_HOLIDAY_MGR H
	ON D.SUN_YMD = H.HOLIDAY_YMD
	AND H.TENANT_ID = #{tenantId}
	AND H.ENTER_CD = #{enterCd}
	WHERE M.FLEXIBLE_STD_MGR_ID = #{flexibleStdMgrId}
	  AND D.SUN_YMD BETWEEN #{sYmd} AND #{eYmd} 
	ORDER BY D.SUN_YMD
) X

SELECT A.APPL_ID AS applId , A.APPL_CD AS applCd , C.APPL_NM AS applNm , A.APPL_SABUN AS applSabun , F_WTM_GET_EMP_NM(A.TENANT_ID, A.ENTER_CD, A.APPL_SABUN, A.APPL_YMD ) AS empNm , A.APPL_YMD AS applYmd , A.APPL_IN_SABUN AS applInSabun , A.APPL_STATUS_CD AS applStatusCd , F_WTM_GET_CODE_NM(A.TENANT_ID, A.ENTER_CD, 'APPL_STATUS_CD', A.APPL_STATUS_CD, A.APPL_YMD) AS applStatusNm , L.APPR_SEQ AS apprSeq , L.APPR_SABUN AS apprSabun , L.APPR_DATE AS apprDate , L.APPR_STATUS_CD AS apprStatusCd , L.APPR_TYPE_CD AS apprTypeCd , L.APPR_OPINION AS apprOpinion , CONCAT(A.ENTER_CD, '@', A.APPL_CD, '@', A.APPL_ID, '@', #{typeCd}, '@', A.APPL_SABUN, '@WTM') AS applKey , CASE WHEN A.APPL_CD = 'OT' THEN 'view://W02View' WHEN A.APPL_CD = 'ENTRY_CHG' THEN 'view://W04View' WHEN A.APPL_CD = 'COMP' THEN 'view://W04View' WHEN A.APPL_CD = 'COMP_CAN' THEN 'view://W04View' WHEN A.APPL_CD IN ('ANNUAL', 'ANNUAL_CAN') THEN 'view://W10View' WHEN A.APPL_CD IN ('REGA', 'REGA_CAN') THEN 'view://W11View'
ELSE NULL END AS urlLink FROM WTM_APPL A JOIN WTM_APPL_CODE C ON A.TENANT_ID = C.TENANT_ID AND A.ENTER_CD = C.ENTER_CD AND A.APPL_CD = C.APPL_CD JOIN WTM_APPL_LINE L ON L.APPL_ID = A.APPL_ID WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND APPR_SABUN = #{empNo} AND L.APPR_TYPE_CD <> 1 AND L.APPR_DATE IS NOT NULL AND L.APPR_STATUS_CD IS NOT NULL AND L.APPR_STATUS_CD IN ('20', '30') ORDER BY A.APPL_YMD DESC
LIMIT #{startPage}, #{pageCount}

