APPL_NM AS applNm , F_WTM_GET_EMP_NM(A.TENANT_ID, A.ENTER_CD, A.APPL_SABUN, A.APPL_YMD ) AS empNm , A.APPL_YMD AS applYmd , A.APPL_IN_SABUN AS applInSabun , A.APPL_STATUS_CD AS applStatusCd , F_WTM_GET_CODE_NM(A.TENANT_ID, A.ENTER_CD, 'APPL_STATUS_CD', A.APPL_STATUS_CD, A.APPL_YMD) AS applStatusNm , A.APPL_YMD AS ymd , F_WTM_DATE_FORMAT(B.SUBS_SDATE,'YMD') AS symd , F_WTM_DATE_FORMAT(B.SUBS_EDATE,'YMD') AS eymd , C.REASON AS reason , A.TENANT_ID AS tenantId , A.ENTER_CD AS enterCd FROM WTM_APPL A INNER JOIN WTM_OT_SUBS_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_OT_APPL C ON B.OT_APPL_ID = C.OT_APPL_ID INNER JOIN WTM_APPL_CODE Z ON A.TENANT_ID = Z.TENANT_ID AND A.ENTER_CD = Z.ENTER_CD AND A.APPL_CD = Z.APPL_CD WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND A.APPL_SABUN = #{empNo} AND A.APPL_CD = 'SUBS_CHG' UNION ALL SELECT A.APPL_ID AS applId , A.APPL_CD AS applCd , A.APPL_SABUN AS applSabun , Z.APPL_NM AS applNm , F_WTM_GET_EMP_NM(A.TENANT_ID, A.ENTER_CD, A.APPL_SABUN, A.APPL_YMD ) AS empNm , A.APPL_YMD AS applYmd , A.APPL_IN_SABUN AS applInSabun , A.APPL_STATUS_CD AS applStatusCd , F_WTM_GET_CODE_NM(A.TENANT_ID, A.ENTER_CD, 'APPL_STATUS_CD', A.APPL_STATUS_CD, A.APPL_YMD) AS applStatusNm , A.APPL_YMD AS ymd , F_WTM_DATE_FORMAT(B.SYMD,'YMD') AS symd , F_WTM_DATE_FORMAT(B.EYMD,'YMD') AS eymd , B.REASON AS reason , A.TENANT_ID AS tenantId , A.ENTER_CD AS enterCd FROM WTM_APPL A INNER JOIN WTM_FLEXIBLE_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_APPL_CODE Z ON A.TENANT_ID = Z.TENANT_ID AND A.ENTER_CD = Z.ENTER_CD AND A.APPL_CD = Z.APPL_CD WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND A.APPL_SABUN = #{empNo} ) T LEFT OUTER JOIN WTM_EMP_HIS T2 ON T.tenantId = T2.TENANT_ID AND T.enterCd = T2.ENTER_CD AND T.applSabun = T2.SABUN AND F_WTM_DATE_FORMAT(NOW(),'YMD') BETWEEN T2.SYMD AND T2.EYMD WHERE 1=1
AND T.applYmd >= #{sYmd}
AND T.applYmd <= #{eYmd}
