mapper.select:
SELECT B.TENANT_ID AS tenantId , B.ENTER_CD AS enterCd , A.SABUN AS sabun , CASE WHEN A.CANCEL_YN = 'Y' THEN '44' ELSE B.APPL_STATUS_CD END AS applStatusCd , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, A.SABUN, A.YMD) AS orgNm , C.EMP_NM AS empNm , CASE WHEN A.HOLIDAY_YN = 'Y' THEN '휴일근무' ELSE '연장근무' END AS applNm , A.YMD AS ymd , F_WTM_DATE_FORMAT(A.OT_SDATE, 'YMDHI') AS otSdate , F_WTM_DATE_FORMAT(A.OT_EDATE, 'YMDHI') AS otEdate , A.OT_MINUTE AS otMinute , A.REASON_CD AS reasonCd , A.REASON AS reason , F_WTM_DATE_FORMAT(D.SUBS_SDATE, 'YMDHI') AS subsSdate , F_WTM_DATE_FORMAT(D.SUBS_EDATE, 'YMDHI') AS subsEdate , B.APPL_ID AS applId FROM WTM_OT_APPL A INNER JOIN WTM_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_EMP_HIS C ON B.TENANT_ID = C.TENANT_ID AND B.ENTER_CD = C.ENTER_CD AND A.SABUN = C.SABUN AND B.APPL_YMD BETWEEN C.SYMD AND C.EYMD LEFT OUTER JOIN WTM_OT_SUBS_APPL D ON A.OT_APPL_ID = D.OT_APPL_ID WHERE B.TENANT_ID = #{tenantId} AND B.ENTER_CD = #{enterCd} AND A.YMD BETWEEN #{sYmd} AND #{eYmd}
AND (C.SABUN = #{searchKeyword} OR C.EMP_NM LIKE CONCAT(#{searchKeyword}, '%'))
AND C.ORG_CD IN (NULL,
#{org}
)

SELECT B.TENANT_ID AS tenantId , B.ENTER_CD AS enterCd , B.APPL_SABUN AS sabun , B.APPL_STATUS_CD AS applStatusCd , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, B.APPL_SABUN, A.YMD) AS orgNm , C.EMP_NM AS empNm , A.YMD AS ymd , F_WTM_DATE_FORMAT(A.PLAN_SDATE, 'YMDHI') AS planSdate , F_WTM_DATE_FORMAT(A.PLAN_EDATE, 'YMDHI') AS planEdate , F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'YMDHI') AS entrySdate , F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'YMDHI') AS entryEdate , F_WTM_DATE_FORMAT(A.CHG_SDATE, 'YMDHI') AS chgSdate , F_WTM_DATE_FORMAT(A.CHG_EDATE, 'YMDHI') AS chgEdate , A.REASON AS reason FROM WTM_ENTRY_APPL A INNER JOIN WTM_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_EMP_HIS C ON B.TENANT_ID = C.TENANT_ID AND B.ENTER_CD = C.ENTER_CD AND B.APPL_SABUN = C.SABUN AND B.APPL_YMD BETWEEN C.SYMD AND C.EYMD WHERE B.TENANT_ID = #{tenantId} AND B.ENTER_CD = #{enterCd} AND A.YMD BETWEEN #{sYmd} AND #{eYmd}
AND (C.SABUN = #{searchKeyword} OR C.EMP_NM LIKE CONCAT(#{searchKeyword}, '%'))
AND C.ORG_CD IN (NULL,
#{org}
)
