mapper.select:
SELECT B.TENANT_ID AS tenantId , B.ENTER_CD AS enterCd , C.SABUN AS sabun , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, A.YMD) AS orgNm , C.EMP_NM AS empNm , DECODE(A.HOLIDAY_YN, 'Y', '휴일근무', '연장근무') AS applNm , A.YMD AS ymd , A.OT_SDATE AS otSdate , A.OT_EDATE AS otEdate , A.OT_MINUTE AS otMinute , A.REASON_CD AS reasonCd , A.REASON AS reason , D.SUBS_SDATE AS subsSdate , D.SUBS_EDATE AS subsEdate FROM WTM_OT_APPL A INNER JOIN WTM_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_EMP_HIS C ON B.TENANT_ID = C.TENANT_ID AND B.ENTER_CD = C.ENTER_CD AND B.APPL_SABUN = C.SABUN AND B.APPL_YMD BETWEEN C.SYMD AND C.EYMD LEFT OUTER JOIN WTM_OT_SUBS_APPL D ON A.OT_APPL_ID = D.OT_APPL_ID WHERE B.TENANT_ID = #{tenantId} AND B.ENTER_CD = #{enterCd} AND A.YMD BETWEEN #{sYmd} AND #{eYmd}
AND (C.SABUN = #{searchKeyword} OR C.EMP_NM LIKE #{searchKeyword} || '%')
AND C.ORG_CD IN (NULL,
#{org}
)
