mapper.select:
SELECT A.WORK_CALENDAR_ID as workCalendarId, A.TENANT_ID as tenantId, A.ENTER_CD as enterCd, A.SABUN as sabun, F_WTM_GET_EMP_ORG_NM(#{tenantId}, #{enterCd}, A.SABUN, F_WTM_DATE_FORMAT(now(),'YMD')) AS orgNm, D.EMP_NM AS empNm, A.YMD as ymd, A.TIME_CD_MGR_ID as timeCdMgrId, F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'HI') as entrySdate, A.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'HI') as entryEdate, A.ENTRY_ETYPE_CD as entryEtypeCd, A.WORK_CLOSE_YN as workCloseYn, A.HOLIDAY_YN as holidayYn, A.NOTE as note, B.FLEXIBLE_STD_MGR_ID as flexibleStdMgrId, B.FLEXIBLE_EMP_ID as flexibleEmpId, D.EMP_NM as empNm, SUM(CASE WHEN A.YMD > F_WTM_DATE_FORMAT(now(),'YMD') AND C.TIME_TYPE_CD NOT IN ('EXCEPT', 'LLA', 'GOBACK') THEN PLAN_MINUTE WHEN A.YMD < F_WTM_DATE_FORMAT(now(),'YMD') AND C.TIME_TYPE_CD NOT IN ('EXCEPT', 'LLA', 'GOBACK') THEN APPR_MINUTE WHEN (A.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && A.ENTRY_EDATE IS NOT NULL) AND C.TIME_TYPE_CD NOT IN ('EXCEPT', 'LLA', 'GOBACK') THEN APPR_MINUTE WHEN (A.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && A.ENTRY_EDATE IS NULL) AND C.TIME_TYPE_CD NOT IN ('EXCEPT', 'LLA', 'GOBACK') THEN PLAN_MINUTE ELSE 0 END) - SUM(CASE WHEN A.YMD > F_WTM_DATE_FORMAT(now(),'YMD') AND C.TIME_TYPE_CD = 'EXCEPT' THEN PLAN_MINUTE WHEN A.YMD < F_WTM_DATE_FORMAT(now(),'YMD') AND C.TIME_TYPE_CD = 'EXCEPT' THEN APPR_MINUTE WHEN (A.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && A.ENTRY_EDATE IS NOT NULL) AND C.TIME_TYPE_CD = 'EXCEPT' THEN APPR_MINUTE WHEN (A.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && A.ENTRY_EDATE IS NULL) AND C.TIME_TYPE_CD = 'EXCEPT' THEN PLAN_MINUTE ELSE 0 END) AS totTime, E.CORE_SHM as coreShm, E.CORE_EHM as coreEhm, GROUP_CONCAT(CASE WHEN C.TIME_TYPE_CD IN ('TAA','REGA','SUBS','LLA') THEN C.TAA_NM ELSE NULL END ORDER BY C.APPR_SDATE) AS taaNms FROM WTM_WORK_CALENDAR A JOIN WTM_FLEXIBLE_EMP B ON B.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=B.ENTER_CD AND A.SABUN=B.SABUN AND A.YMD BETWEEN B.SYMD AND B.EYMD LEFT JOIN ( SELECT R.* , T.TAA_NM FROM WTM_WORK_DAY_RESULT R LEFT OUTER JOIN WTM_TAA_CODE T ON R.TENANT_ID = T.TENANT_ID AND R.ENTER_CD = T.ENTER_CD AND R.TAA_CD = T.TAA_CD WHERE R.TENANT_ID = #{tenantId} AND R.ENTER_CD = #{enterCd} AND R.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '') ) C ON C.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=C.ENTER_CD AND A.SABUN=C.SABUN AND C.YMD = A.YMD JOIN WTM_EMP_HIS D ON D.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=D.ENTER_CD AND A.SABUN=D.SABUN AND F_WTM_DATE_FORMAT(now(),'YMD') BETWEEN D.SYMD AND D.EYMD JOIN WTM_FLEXIBLE_STD_MGR E ON E.FLEXIBLE_STD_MGR_ID = B.FLEXIBLE_STD_MGR_ID WHERE A.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '') AND #{tenantId} = A.TENANT_ID AND A.ENTER_CD = #{enterCd}
AND D.ORG_CD IN (NULL,
#{org}
)
AND (A.SABUN LIKE CONCAT('%', #{searchKeyword}, '%') OR D.EMP_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
GROUP BY A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD ORDER BY YMD DESC, SABUN

SELECT * FROM ( SELECT C.WORK_CALENDAR_ID as workCalendarId, C.TENANT_ID as tenantId, C.ENTER_CD as enterCd, C.SABUN as sabun, O.ORG_NM AS orgNm, H.EMP_NM AS empNm, C.YMD as ymd, C.TIME_CD_MGR_ID as timeCdMgrId, F_WTM_DATE_FORMAT(C.ENTRY_SDATE, 'HI') as entrySdate, C.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(C.ENTRY_EDATE, 'HI') as entryEdate, C.ENTRY_ETYPE_CD as enteryEtypeCd, C.WORK_CLOSE_YN as workCloseYn, C.HOLIDAY_YN as holidayYn, C.NOTE as note, E.FLEXIBLE_STD_MGR_ID as flexibleStdMgrId, CASE WHEN C.YMD > F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(PLAN_MINUTE) WHEN C.YMD < F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(APPR_MINUTE) WHEN (C.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && C.ENTRY_EDATE IS NOT NULL) THEN SUM(APPR_MINUTE) ELSE SUM(PLAN_MINUTE) END AS totTime FROM WTM_WORK_CALENDAR C JOIN WTM_FLEXIBLE_EMP E ON E.TENANT_ID = C.TENANT_ID AND E.ENTER_CD = C.ENTER_CD AND E.SABUN = C.SABUN AND C.YMD BETWEEN E.SYMD AND E.EYMD LEFT JOIN WTM_WORK_DAY_RESULT R ON C.TENANT_ID = R.TENANT_ID AND C.ENTER_CD = R.ENTER_CD AND C.SABUN=R.SABUN AND C.YMD = R.YMD JOIN WTM_EMP_HIS H ON C.TENANT_ID = H.TENANT_ID AND C.ENTER_CD = H.ENTER_CD AND C.SABUN = H.SABUN AND C.YMD BETWEEN H.SYMD AND H.EYMD LEFT OUTER JOIN WTM_ORG_CODE O ON H.TENANT_ID = O.TENANT_ID AND H.ENTER_CD = O.ENTER_CD AND H.ORG_CD = O.ORG_CD AND C.YMD BETWEEN O.SYMD AND O.EYMD WHERE C.TENANT_ID = #{tenantId} AND C.ENTER_CD = #{enterCd} AND C.YMD BETWEEN #{sYmd} AND #{eYmd}
AND H.ORG_CD IN (NULL,
#{org}
)
GROUP BY C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD ) T
WHERE (T.sabun LIKE CONCAT('%', #{searchKeyword}, '%') OR T.empNm LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY YMD DESC, SABUN

        
		      SELECT F_WTM_NVL(B.UNPLANNED_YN, 'N') AS unplannedYn
			  FROM WTM_FLEXIBLE_EMP A
			 INNER JOIN WTM_FLEXIBLE_STD_MGR B
			    ON A.FLEXIBLE_STD_MGR_ID = B.FLEXIBLE_STD_MGR_ID
			 WHERE A.TENANT_ID = #{tenantId}
			   AND A.ENTER_CD = #{enterCd}
			   AND A.SABUN = #{sabun}
			   AND #{ymd} BETWEEN A.SYMD AND A.EYMD

