SET 
	
		
		ENTRY_SDATE=F_WTM_TO_DATE(#{chgSdate}, 'YMDHIS'), 
		ENTRY_STYPE_CD = #{typeCd},
		
		
		ENTRY_EDATE=F_WTM_TO_DATE(#{chgEdate}, 'YMDHIS'), 
		ENTRY_ETYPE_CD = #{typeCd},
		
					
	
	ENTRY_SDATE = null,
	ENTRY_EDATE = null,
	ENTRY_STYPE_CD = null,
	ENTRY_ETYPE_CD = null,
	
	
	UPDATE_DATE = now(),
	UPDATE_ID = #{userId}  
WHERE TENANT_ID = #{tenantId} 
	AND ENTER_CD = #{enterCd}
	AND SABUN = #{sabun}
	AND YMD = #{ymd}

