mapper.select:
SELECT A.WORK_CALENDAR_ID as workCalendarId, A.TENANT_ID as tenantId, A.ENTER_CD as enterCd, A.SABUN as sabun, F_WTM_GET_EMP_ORG_NM(#{tenantId}, #{enterCd}, A.SABUN, F_WTM_DATE_FORMAT(now(),'YMD')) AS orgNm, F_WTM_GET_EMP_NM(#{tenantId}, #{enterCd}, A.SABUN, F_WTM_DATE_FORMAT(now(),'YMD')) AS empNm, A.YMD as ymd, A.TIME_CD_MGR_ID as timeCdMgrId, F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'HI') as entrySdate, A.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'HI') as entryEdate, A.ENTRY_ETYPE_CD as entryEtypeCd, A.WORK_CLOSE_YN as workCloseYn, A.HOLIDAY_YN as holidayYn, A.NOTE as note, B.FLEXIBLE_STD_MGR_ID as flexibleStdMgrId, D.EMP_NM as empNm, CASE WHEN A.YMD > F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(PLAN_MINUTE) WHEN A.YMD < F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(APPR_MINUTE) WHEN (A.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && A.ENTRY_EDATE IS NOT NULL) THEN SUM(APPR_MINUTE) ELSE SUM(PLAN_MINUTE) END AS totTime, E.CORE_SHM as coreShm, E.CORE_EHM as coreEhm FROM WTM_WORK_CALENDAR A JOIN WTM_FLEXIBLE_EMP B ON B.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=B.ENTER_CD AND A.SABUN=B.SABUN AND A.YMD BETWEEN B.SYMD AND B.EYMD LEFT JOIN WTM_WORK_DAY_RESULT C ON C.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=C.ENTER_CD AND A.SABUN=C.SABUN AND C.YMD = A.YMD JOIN WTM_EMP_HIS D ON D.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=D.ENTER_CD AND A.SABUN=D.SABUN AND F_WTM_DATE_FORMAT(now(),'YMD') BETWEEN D.SYMD AND D.EYMD JOIN WTM_FLEXIBLE_STD_MGR E ON E.FLEXIBLE_STD_MGR_ID = B.FLEXIBLE_STD_MGR_ID WHERE A.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '') AND #{tenantId} = A.TENANT_ID AND A.ENTER_CD = #{enterCd}
AND D.ORG_CD IN (NULL,
#{org}
)
AND (A.SABUN LIKE CONCAT('%', #{searchKeyword}, '%') OR D.EMP_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
GROUP BY A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD ORDER BY YMD DESC, SABUN

SELECT * FROM ( SELECT C.WORK_CALENDAR_ID as workCalendarId, C.TENANT_ID as tenantId, C.ENTER_CD as enterCd, C.SABUN as sabun, F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS orgNm, F_WTM_GET_EMP_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS empNm, C.YMD as ymd, C.TIME_CD_MGR_ID as timeCdMgrId, F_WTM_DATE_FORMAT(C.ENTRY_SDATE, 'HI') as entrySdate, C.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(C.ENTRY_EDATE, 'HI') as entryEdate, C.ENTRY_ETYPE_CD as enteryEtypeCd, C.WORK_CLOSE_YN as workCloseYn, C.HOLIDAY_YN as holidayYn, C.NOTE as note, E.FLEXIBLE_STD_MGR_ID as flexibleStdMgrId, CASE WHEN C.YMD > F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(PLAN_MINUTE) WHEN C.YMD < F_WTM_DATE_FORMAT(now(),'YMD') THEN SUM(APPR_MINUTE) WHEN (C.YMD = F_WTM_DATE_FORMAT(now(),'YMD') && C.ENTRY_EDATE IS NOT NULL) THEN SUM(APPR_MINUTE) ELSE SUM(PLAN_MINUTE) END AS totTime FROM WTM_WORK_CALENDAR C JOIN WTM_FLEXIBLE_EMP E ON E.TENANT_ID = C.TENANT_ID AND E.ENTER_CD = C.ENTER_CD AND E.SABUN = C.SABUN AND C.YMD BETWEEN E.SYMD AND E.EYMD LEFT JOIN WTM_WORK_DAY_RESULT R ON C.TENANT_ID = R.TENANT_ID AND C.ENTER_CD = R.ENTER_CD AND C.SABUN=R.SABUN AND C.YMD = R.YMD JOIN WTM_EMP_HIS H ON C.TENANT_ID = H.TENANT_ID AND C.ENTER_CD = H.ENTER_CD AND C.SABUN = H.SABUN AND C.YMD BETWEEN H.SYMD AND H.EYMD WHERE C.TENANT_ID = #{tenantId} AND C.ENTER_CD = #{enterCd} AND C.YMD BETWEEN #{sYmd} AND #{eYmd} AND H.ORG_CD IN (NULL,
#{org}
) GROUP BY C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD ) T
WHERE (T.sabun LIKE CONCAT('%', #{searchKeyword}, '%') OR T.empNm LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY YMD DESC, SABUN

{ call P_WTM_UPDATE_TIMECARD( 
	#{tenantId, jdbcType=BIGINT, javaType=java.lang.Long, mode=IN}, 
	#{enterCd, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{sabun, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{ymd, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{inoutType, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{entryType, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{rtnYmd, jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT},
	#{sqlErrm, jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT} 
) }

if.foreach:
UPDATE WTM_WORK_CALENDAR
SET 
	
	ENTRY_SDATE=F_WTM_TO_DATE(#{item.chgSdate}, 'YMDHIS'), 
	ENTRY_STYPE_CD = #{typeCd},
	
	
	ENTRY_EDATE=F_WTM_TO_DATE(#{item.chgEdate}, 'YMDHIS'), 
	ENTRY_ETYPE_CD = #{typeCd},
	
	UPDATE_DATE = now(),
	UPDATE_ID = #{userId}  
WHERE TENANT_ID = #{tenantId} 
	AND ENTER_CD = #{enterCd}
	AND SABUN = #{item.sabun}
	AND YMD = #{item.ymd}
