mapper.select:
SELECT * FROM ( SELECT EMP_HIS_ID AS empHisId , ENTER_CD AS enterCd , SABUN AS sabun , EMP_NM AS empNm , EMP_ENG_NM AS empEngNm , SYMD AS symd , EYMD AS eymd , STATUS_CD AS statusCd , ORG_CD AS orgCd , BUSINESS_PLACE_CD AS businessPlaceCd , DUTY_CD AS dutyCd , POS_CD AS posCd , CLASS_CD AS classCd , JOB_GROUP_CD AS jobGroupCd , JOB_CD AS jobCd , PAY_TYPE_CD AS payTypeCd , LEADER_YN AS leaderYn , NOTE AS note , F_WTM_GET_ORG_NM(TENANT_ID, ENTER_CD, ORG_CD, SYMD) AS orgNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'DUTY_CD', DUTY_CD, SYMD) AS dutyNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'POS_CD', POS_CD, SYMD) AS posNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'CLASS_CD', CLASS_CD, SYMD) AS classNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'JOB_CD', JOB_CD, SYMD) AS jobNm , EMP_ID AS empId FROM WTM_EMP_HIS WHERE TENANT_ID = #{tenantId} AND ENTER_CD = #{enterCd} AND CASE WHEN #{sYmd} = '' THEN F_WTM_DATE_FORMAT(now(),'YMD') ELSE REPLACE(#{sYmd}, '-', '') END BETWEEN SYMD AND EYMD AND STATUS_CD <> 'RA'
AND ORG_CD IN (NULL,
#{org}
)
) T WHERE 1=1 AND (sabun LIKE CONCAT(#{searchKeyword}, '%') OR empNm LIKE CONCAT('%', #{searchKeyword}, '%') OR orgNm LIKE CONCAT('%', #{searchKeyword}, '%') OR dutyNm LIKE CONCAT('%', #{searchKeyword}, '%') OR posNm LIKE CONCAT('%', #{searchKeyword}, '%') OR classNm LIKE CONCAT('%', #{searchKeyword}, '%') OR jobNm LIKE CONCAT('%', #{searchKeyword}, '%'))

SELECT EMP_HIS_ID AS empHisId , ENTER_CD AS enterCd , SABUN AS sabun , EMP_NM AS empNm , EMP_ENG_NM AS empEngNm , SYMD AS symd , EYMD AS eymd , STATUS_CD AS statusCd , ORG_CD AS orgCd , BUSINESS_PLACE_CD AS businessPlaceCd , DUTY_CD AS dutyCd , POS_CD AS posCd , CLASS_CD AS classCd , JOB_GROUP_CD AS jobGroupCd , JOB_CD AS jobCd , PAY_TYPE_CD AS payTypeCd , LEADER_YN AS leaderYn , NOTE AS note , F_WTM_GET_ORG_NM(TENANT_ID, ENTER_CD, ORG_CD, SYMD) AS orgNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'DUTY_CD', DUTY_CD, SYMD) AS dutyNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'POS_CD', POS_CD, SYMD) AS posNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'CLASS_CD', CLASS_CD, SYMD) AS classNm , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'JOB_CD', JOB_CD, SYMD) AS jobNm , EMP_ID AS empId FROM WTM_EMP_HIS WHERE EMP_HIS_ID = #{empHisId} AND STATUS_CD <> 'RA'
AND ORG_CD IN (NULL,
#{org}
)

mapper.update:
UPDATE comm_user A JOIN ( SELECT NULL AS TENANT_ID , NULL AS ENTER_CD , NULL AS SABUN , NULL AS PASSWORD FROM DUAL
UNION SELECT ${tenantId} AS TENANT_ID , F_WTM_AES_ENCRYPT(#{enterCd}, #{aesKey}) AS ENTER_CD , F_WTM_AES_ENCRYPT( #{m.sabun}, #{aesKey}) AS SABUN , #{m.password} AS PASSWORD FROM DUAL
) T ON A.tenant_id = T.TENANT_ID AND A.enter_Cd = T.ENTER_CD AND A.login_id = T.SABUN SET A.password = T.PASSWORD WHERE A.tenant_id = T.TENANT_ID AND A.enter_Cd = T.ENTER_CD AND A.login_id = T.SABUN

DELETE FROM comm_user WHERE (tenant_id, enter_cd, login_id) IN ( SELECT TENANT_ID , F_WTM_AES_ENCRYPT(ENTER_CD, #{aesKey}) , F_WTM_AES_ENCRYPT(SABUN, #{aesKey}) FROM WTM_EMP_HIS WHERE EMP_HIS_ID IN (NULL
#{empHisId}
) )
