mapper.select:
  		{ call P_WTM_DATE_ADD_FOR_BREAK_MGR_T( 
  				#{tenantId,  jdbcType=BIGINT,  mode=IN}
			  , #{enterCd,   jdbcType=VARCHAR, mode=IN}
			  , #{timeCd,    jdbcType=BIGINT,  mode=IN}
			  , #{obDate,    jdbcType=VARCHAR, mode=IN}
			  , #{addMinute, jdbcType=INTEGER, mode=IN}
			  , #{retDate,    jdbcType=VARCHAR, mode=OUT}
		) }

  		{ call P_WTM_DATE_ADD_FOR_BREAK_MGR_T2( 
  				#{tenantId, jdbcType=BIGINT,  mode=IN}
			  , #{enterCd,  jdbcType=VARCHAR, mode=IN}
			  , #{timeCd,   jdbcType=BIGINT,  mode=IN}
			  , #{obDate,   jdbcType=VARCHAR, mode=IN}
			  , #{addMinute,jdbcType=INTEGER, mode=IN}
			  , #{retDate,   jdbcType=VARCHAR, mode=OUT}
		) }

SELECT MIN(PLAN_SDATE) AS planSdate
     , MAX(PLAN_EDATE) AS planEdate
     , F_WTM_DATE_FORMAT( MIN(PLAN_SDATE), 'YMD') AS planSday
     , F_WTM_DATE_FORMAT( MAX(PLAN_EDATE), 'YMD') AS planEday
     , F_WTM_DATE_FORMAT(DATE_ADD(MIN(PLAN_SDATE), interval +1 day), 'YMD') AS tempEday
 FROM WTM_WORK_DAY_RESULT 
WHERE TENANT_ID = #{tenantId}
  AND ENTER_CD = #{enterCd}
  AND SABUN = #{sabun}
  AND YMD = #{ymd}
  AND TIME_TYPE_CD IN ('BASE', 'OT', 'FIXOT', 'REGA', 'NIGHT')

 SELECT F_WTM_DATE_FORMAT(MIN(A.PLAN_SDATE),'YMDHI') AS planSdate
      , F_WTM_DATE_FORMAT(MAX(A.PLAN_EDATE),'YMDHI') AS planEdate
      , F_WTM_DATE_FORMAT( MIN(A.PLAN_SDATE), 'YMD') AS planSday
      , F_WTM_DATE_FORMAT( MAX(A.PLAN_EDATE), 'YMD') AS planEday
      , F_WTM_DATE_FORMAT(DATE_ADD(MIN(A.PLAN_SDATE), interval +1 day), 'YMD') AS tempEday
      , A.TIME_TYPE_CD as timeTypeCd
   FROM WTM_WORK_DAY_RESULT_O A
  WHERE A.TENANT_ID = #{tenantId}
    AND A.ENTER_CD = #{enterCd}
	AND A.SABUN = #{sabun}
    AND A.YMD = #{ymd}
    AND A.TIME_TYPE_CD IN ('BASE', 'FIXOT')
  GROUP BY A.TIME_TYPE_CD

 DELETE 
   FROM WTM_WORK_DAY_RESULT
  WHERE TENANT_ID = #{tenantId}
    AND ENTER_CD = #{enterCd}
	AND SABUN = #{sabun}
    AND YMD = #{ymd}
    AND TIME_TYPE_CD = #{timeTypeCd}
