update.if:
DELETE FROM WTM_HOLIDAY_MGR WHERE (TENANT_ID, ENTER_CD, BISINESS_PLACE_CD, HOLIDAY_YMD) IN (
(${tenantId}, #{enterCd}, #{m.bisinessPlaceCd}, #{m.holidayYmd})
)

INSERT INTO WTM_HOLIDAY_MGR(TENANT_ID, ENTER_CD, BISINESS_PLACE_CD, HOLIDAY_YMD, HOLIDAY_NM, SUN_YN, FESTIVE_YN, PAY_YN, NOTE) 

SELECT ${tenantId}, #{enterCd}, #{m.bisinessPlaceCd}, #{m.holidayYmd}, #{m.holidayNm}, #{m.sunYn}, #{m.festiveYn}, #{m.payYn}, #{m.note}
  FROM DUAL

UPDATE WTM_HOLIDAY_MGR M JOIN (
SELECT ${tenantId} AS TENANT_ID , #{enterCd} AS ENTER_CD , #{m.bisinessPlaceCd} AS BISINESS_PLACE_CD , #{m.holidayYmd} AS HOLIDAY_YMD , #{m.holidayNm} AS HOLIDAY_NM , #{m.sunYn} AS SUN_YN , #{m.festiveYn} AS FESTIVE_YN , #{m.payYn} AS PAY_YN , #{m.note} AS NOTE FROM DUAL
) T ON M.TENANT_ID = T.TENANT_ID AND M.ENTER_CD = T.ENTER_CD AND M.BISINESS_PLACE_CD = T.BISINESS_PLACE_CD AND M.HOLIDAY_YMD = T.HOLIDAY_YMD SET M.HOLIDAY_NM = T.HOLIDAY_NM , M.NOTE = T.NOTE
