mapper.select:
SELECT A.SABUN as sabun , B.EMP_NM as empNm, A.YMD as ymd, F_WTM_GET_ORG_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS orgNm, F_WTM_GET_FLEXIBLE_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS flexibleNm, F_WTM_GET_TIME_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS timeNm, F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'YMDHI') as entrySdate, A.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'YMDHI') as entryEdate, A.ENTRY_ETYPE_CD as entryEtypeCd, F_WTM_DATE_FORMAT(A.CHG_SDATE, 'YMDHI') as chgSdate, F_WTM_DATE_FORMAT(A.CHG_EDATE, 'YMDHI') as chgEdate, A.REASON as reason, A.UPDATE_ID as updateId, A.UPDATE_DATE as updateDate FROM WTM_INOUT_CHG_HIS A INNER JOIN WTM_EMP_HIS B ON A.TENANT_ID = B.TENANT_ID AND A.ENTER_CD = B.ENTER_CD AND A.SABUN = B.SABUN AND A.YMD BETWEEN B.SYMD AND B.EYMD WHERE A.TENANT_ID=#{tenantId} AND A.ENTER_CD=#{enterCd} AND A.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '')
AND (A.SABUN LIKE CONCAT(#{searchKeyword}, '%') OR B.EMP_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
AND ORG_CD IN (NULL,
#{org}
)
