mapper.select:
SELECT A.SABUN as sabun , B.EMP_NM as empNm, A.YMD as ymd, F_WTM_GET_ORG_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS orgNm, F_WTM_GET_FLEXIBLE_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS flexibleNm, F_WTM_GET_TIME_NM(A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD) AS timeNm, F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'YMDHI') as entrySdate, A.ENTRY_STYPE_CD as entryStypeCd, F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'YMDHI') as entryEdate, A.ENTRY_ETYPE_CD as entryEtypeCd, F_WTM_DATE_FORMAT(A.CHG_SDATE, 'YMDHI') as chgSdate, F_WTM_DATE_FORMAT(A.CHG_EDATE, 'YMDHI') as chgEdate, A.REASON as reason, A.UPDATE_ID as updateId, A.UPDATE_DATE as updateDate A.ENTRY_DELETE_YN as entryDeleteYn FROM WTM_INOUT_CHG_HIS A INNER JOIN WTM_EMP_HIS B ON A.TENANT_ID = B.TENANT_ID AND A.ENTER_CD = B.ENTER_CD AND A.SABUN = B.SABUN AND A.YMD BETWEEN B.SYMD AND B.EYMD WHERE A.TENANT_ID=#{tenantId} AND A.ENTER_CD=#{enterCd} AND A.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '')
AND (A.SABUN LIKE CONCAT(#{searchKeyword}, '%') OR B.EMP_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
AND ORG_CD IN (NULL,
#{org}
)

SELECT F_WTM_DATE_FORMAT(H.INOUT_DATE, 'YMDHI') AS inoutDate , CASE WHEN H.INOUT_TYPE_CD ='IN' THEN '출근' WHEN H.INOUT_TYPE_CD ='OUT' THEN '퇴근' WHEN H.INOUT_TYPE_CD ='GO' THEN '외출' WHEN H.INOUT_TYPE_CD ='BACK' THEN '복귀' ELSE '' END AS inoutType , H.SABUN AS sabun , F_WTM_DATE_FORMAT(H.YMD, 'YMDHI') AS ymd , E.EMP_NM AS empNm , O.ORG_NM AS orgNm FROM WTM_INOUT_HIS H JOIN WTM_EMP_HIS E ON E.TENANT_ID = H.TENANT_ID AND E.ENTER_CD = H.ENTER_CD AND E.SABUN = H.SABUN AND #{ymd} BETWEEN E.SYMD AND E.EYMD JOIN WTM_ORG_CODE O ON O.TENANT_ID = E.TENANT_ID AND O.ENTER_CD = E.ENTER_CD AND O.ORG_CD = E.ORG_CD AND #{ymd} BETWEEN O.SYMD AND O.EYMD WHERE DATE_FORMAT(H.INOUT_DATE, '%Y%m%d') = #{ymd} AND H.TENANT_ID=#{tenantId} AND H.ENTER_CD=#{enterCd}
AND (E.SABUN LIKE CONCAT(#{searchValue}, '%') OR E.EMP_NM LIKE CONCAT('%', #{searchValue}, '%'))
AND E.ORG_CD IN (NULL,
#{org}
)
ORDER BY H.INOUT_DATE DESC

mapper.update:
UPDATE WTM_WORK_DAY_RESULT 
SET APPR_SDATE = NULL, APPR_EDATE = NULL, APPR_MINUTE = NULL
WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND SABUN = #{sabun}
   AND YMD = #{stdYmd}
   AND TIME_TYPE_CD IN ('BASE','OT','FIXOT','NIGHT', 'GOBACK')
