SELECT A.LEADER_CNT as leaderCnt, B.CONC_CNT as concCnt, C.CONC_CNT as concEndCnt
  FROM (SELECT COUNT(*) AS LEADER_CNT
					  FROM WTM_EMP_HIS
					 WHERE TENANT_ID = #{tenantId}
					   AND ENTER_CD = #{enterCd}
					   AND ORG_CD = #{orgCd}
					   AND SABUN = #{sabun}
					   AND LEADER_YN = 'Y'
					   AND EYMD = #{eymd}) A
 INNER JOIN (SELECT COUNT(*) AS CONC_CNT
			  FROM WTM_ORG_CONC
			 WHERE TENANT_ID = #{tenantId}
			   AND ENTER_CD = #{enterCd}
			   AND ORG_CD = #{orgCd}
			   AND SABUN = #{sabun}
			   AND SYMD = #{symd}
			   AND EYMD = #{eymd}) B
INNER JOIN (SELECT COUNT(*) AS CONC_CNT
			  FROM WTM_ORG_CONC
			 WHERE TENANT_ID = #{tenantId}
			   AND ENTER_CD = #{enterCd}
			   AND ORG_CD = #{orgCd}
			   AND SABUN = #{sabun}
			   AND SYMD = #{symd}
			   AND EYMD <> #{eymd}) C
    ON 1=1

