	  , #{retCode,jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT}
	  , #{retMsg,jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT}
	  ) }


	SELECT 	WORKTIME_CLOSE_ID worktimeCloseId
			, SABUN sabun
			, WORK_TYPE_CD workTypeCd
	FROM 	WTM_WORKTIME_DAY_CLOSE
	WHERE 	WORKTIME_CLOSE_ID = #{worktimeCloseId}
	AND 	YMD BETWEEN #{sYmd} and #{eYmd}


	AND 	SABUN = #{sabun}

GROUP BY WORKTIME_CLOSE_ID, SABUN, WORK_TYPE_CD
ORDER BY SABUN


SELECT IFNULL(SUM(C.SUBS_MINUTE), 0) subsMinute
 FROM WTM_OT_APPL B
 INNER JOIN WTM_OT_SUBS_APPL C
	ON B.OT_APPL_ID = C.OT_APPL_ID
   AND F_WTM_NVL(C.CANCEL_YN, 'N') = 'N'
 INNER JOIN WTM_APPL D
	ON C.APPL_ID = D.APPL_ID
   AND D.APPL_STATUS_CD = '99'
 WHERE D.TENANT_ID = #{tenantId}
   AND D.ENTER_CD = #{enterCd}
   AND B.SABUN = #{sabun}
   AND F_WTM_NVL(B.CANCEL_YN, 'N') = 'N'
   AND B.YMD BETWEEN #{sYmd} AND #{eYmd}
   AND C.SUB_YMD BETWEEN #{sYmd} AND #{eYmd}

mapper.delete:

DELETE FROM WTM_WORKTIME_MON_CLOSE
WHERE WORKTIME_CLOSE_ID = #{worktimeCloseId}
AND SABUN = #{sabun}
AND WORK_TYPE_CD = #{workTypeCd}

mapper.select:
SELECT A.FIXOT_USE_LIMIT AS n_fix_ot_minute
FROM WTM_FLEXIBLE_STD_MGR A
		 INNER JOIN WTM_FLEXIBLE_EMP B
					ON A.FLEXIBLE_STD_MGR_ID = B.FLEXIBLE_STD_MGR_ID
WHERE B.TENANT_ID = #{tenantId}
  AND B.ENTER_CD = #{enterCd}
  AND B.SABUN = #{sabun}
  AND #{sYmd} BETWEEN B.SYMD AND B.EYMD
  AND B.WORK_TYPE_CD = #{workTypeCd}

SELECT R.ENTER_CD , R.SABUN , ( SELECT COUNT(DISTINCT D.YMD) FROM WTM_WORK_DAY_RESULT D WHERE D.TENANT_ID = ${tenantId} AND D.ENTER_CD = #{enterCd} AND D.YMD BETWEEN #{symd} AND #{eymd} AND (#{sabun} IS NULL OR #{sabun} = '' OR D.SABUN = #{sabun}) AND D.TIME_TYPE_CD != 'SUBS' ) AS WORK_DAYS , SUM(CASE WHEN (TIME_TYPE_CD IN ('BASE', 'REGA') OR (F_WTM_GET_EMP_DAY_OPTION(R.TENANT_ID, R.ENTER_CD, R.SABUN, R.YMD, 'TAA_TIME_YN') = 'Y' AND TIME_TYPE_CD = 'TAA')) THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS WORK_MINUTE , SUM(CASE WHEN TIME_TYPE_CD IN ('OT', 'FIXOT') THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS OT_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'NIGHT' THEN F_WTM_NVL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS OTN_MINUTE , SUM(CASE WHEN (TIME_TYPE_CD IN ('OT') AND A.HOLIDAY_YN = 'Y' ) THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS HOLIDAY_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'EXCEPT' AND R.TAA_CD IN ('BREAK_OT', 'BREAK_FIXOT', 'BREAK', 'BREAK_NIGHT') THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS ALL_BREAK_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'EXCEPT' AND R.TAA_CD = 'BREAK' THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS BREAK_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'EXCEPT' AND R.TAA_CD IN ('BREAK_OT', 'BREAK_FIXOT') THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS BREAK_OT_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'EXCEPT' AND R.TAA_CD = 'BREAK_NIGHT' THEN IFNULL(TRUNCATE(APPR_MINUTE/60,1),0) ELSE 0 END) AS BREAK_NIGHT_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'GOBACK' THEN TRUNCATE(R.APPR_MINUTE/60,1) ELSE 0 END) AS GOBACK_MINUTE , SUM(CASE WHEN TIME_TYPE_CD = 'SUBS' THEN TRUNCATE(R.APPR_MINUTE/60,1) ELSE 0 END) AS SUBS_MINUTE FROM WTM_WORK_DAY_RESULT R LEFT OUTER JOIN WTM_TAA_CODE C ON R.TENANT_ID = C.TENANT_ID AND R.ENTER_CD = C.ENTER_CD AND R.TAA_CD = C.TAA_CD LEFT OUTER JOIN WTM_WORK_CALENDAR A ON R.TENANT_ID = A.TENANT_ID AND R.ENTER_CD = A.ENTER_CD AND R.SABUN = A.SABUN AND R.YMD = A.YMD WHERE R.TENANT_ID = ${tenantId} AND R.ENTER_CD = #{enterCd} AND R.YMD BETWEEN #{symd} AND #{eymd}
AND ( #{sabun} IS NULL OR #{sabun} = '' OR R.SABUN = #{sabun} )
GROUP BY R.ENTER_CD, R.SABUN
