SELECT TENANT_ID AS tenantId , ENTER_CD AS enterCd , ORG_CD AS orgCd , ORG_NM AS orgNm , SYMD AS symd , EYMD AS eymd , ORG_TYPE AS orgType , NOTE AS note FROM WTM_ORG_CODE WHERE TENANT_ID = #{tenantId} AND ENTER_CD = #{enterCd} AND (ORG_CD LIKE CONCAT('%', #{orgKeyword}, '%') OR ORG_NM LIKE CONCAT('%', #{orgKeyword}, '%')) AND CASE WHEN #{sYmd} = '' THEN F_WTM_DATE_FORMAT(now(),'YMD') ELSE REPLACE(#{sYmd}, '-', '') END BETWEEN SYMD AND EYMD
AND ORG_CD IN (NULL,
#{org}
)


        	SELECT ORG_CD AS code
				 , ORG_NM AS codeNm
			  FROM WTM_ORG_CODE
			 WHERE TENANT_ID = #{tenantId}
			   AND ENTER_CD = #{enterCd} 
			   AND F_WTM_DATE_FORMAT(now(),'YMD') BETWEEN SYMD AND EYMD


			   AND ORG_CD IN (NULL, 

				   #{org}

			   )
