mapper.select:
SELECT A.TENANT_ID AS tenantId , A.ENTER_CD AS enterCd , A.APPL_STATUS_CD AS applStatusCd , A.APPL_ID AS applId , E.ORG_NM AS orgNm , D.SABUN AS sabun , D.EMP_NM AS empNm , G.TAA_CD AS taaCd , G.TAA_NM AS taaNm , F_WTM_DATE_FORMAT(C.SYMD, 'YMD') AS taaSdate , F_WTM_DATE_FORMAT(C.EYMD, 'YMD') AS taaEdate , F.YMD AS ymd , C.TAA_APPL_ID AS taaApplId FROM WTM_APPL A INNER JOIN WTM_TAA_APPL B ON A.TENANT_ID = B.TENANT_ID AND A.ENTER_CD = B.ENTER_CD AND A.APPL_ID = B.APPL_ID INNER JOIN WTM_TAA_APPL_DET C ON B.TAA_APPL_ID = C.TAA_APPL_ID INNER JOIN WTM_EMP_HIS D ON A.TENANT_ID = D.TENANT_ID AND A.ENTER_CD = D.ENTER_CD AND A.APPL_SABUN = D.SABUN AND A.APPL_YMD BETWEEN D.SYMD AND D.EYMD INNER JOIN WTM_ORG_CODE E ON D.TENANT_ID = E.TENANT_ID AND D.ENTER_CD = E.ENTER_CD AND D.ORG_CD = E.ORG_CD AND E.SYMD BETWEEN D.SYMD AND D.EYMD INNER JOIN WTM_WORK_DAY_RESULT F ON A.TENANT_ID = F.TENANT_ID AND A.ENTER_CD = F.ENTER_CD AND A.APPL_SABUN = F.SABUN AND A.APPL_ID = F.APPL_ID INNER JOIN WTM_TAA_CODE G ON G.TENANT_ID = A.TENANT_ID AND G.ENTER_CD = A.ENTER_CD AND G.TAA_CD = F.TAA_CD WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND A.APPL_STATUS_CD = '99' AND F.YMD BETWEEN #{sYmd} AND #{eYmd}
AND (D.SABUN = #{searchKeyword} OR D.EMP_NM LIKE CONCAT(#{searchKeyword}, '%'))
AND E.ORG_CD IN (NULL,
#{org}
)
GROUP BY A.APPL_ID ORDER BY A.APPL_ID

mapper.update:
DELETE FROM WTM_APPL
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND APPL_ID =  #{applId}

DELETE FROM WTM_TAA_APPL
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND APPL_ID =  #{applId}

UPDATE WTM_WORK_CALENDAR
   SET ENTRY_SDATE = NULL
	 , ENTRY_STYPE_CD = NULL
	 , ENTRY_EDATE = NULL
	 , ENTRY_ETYPE_CD = NULL
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND YMD BETWEEN #{taaSdate} AND #{taaEdate}
   AND SABUN = #{updtSabun}
