mapper.select:
SELECT C.TENANT_ID as tenantId , C.ENTER_CD as enterCd , C.SABUN as sabun , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS orgNm , F_WTM_GET_EMP_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS empNm , C.YMD as ymd , F_WTM_GET_FLEXIBLE_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS flexibleNm , C.TIME_CD_MGR_ID as timeCdMgrId , F_WTM_DATE_FORMAT(C.ENTRY_SDATE, 'YMDHI') as entrySdate , C.ENTRY_STYPE_CD as entryStypeCd , F_WTM_DATE_FORMAT(C.ENTRY_EDATE, 'YMDHI') as entryEdate , C.ENTRY_ETYPE_CD as enteryEtypeCd , C.WORK_CLOSE_YN as workCloseYn , C.HOLIDAY_YN as holidayYn , C.NOTE as note FROM WTM_WORK_CALENDAR C JOIN WTM_EMP_HIS H ON C.TENANT_ID = H.TENANT_ID AND C.ENTER_CD = H.ENTER_CD AND C.SABUN = H.SABUN AND C.YMD BETWEEN H.SYMD AND H.EYMD JOIN ( SELECT TENANT_ID , ENTER_CD , SABUN , YMD , MIN(PLAN_SDATE) AS PLAN_SDATE , MAX(PLAN_EDATE) AS PLAN_EDATE FROM WTM_WORK_DAY_RESULT WHERE TENANT_ID = #{tenantId} AND ENTER_CD = #{enterCd} AND TIME_TYPE_CD IN ('BASE', 'OT') AND YMD BETWEEN #{sYmd} AND #{eYmd} AND (APPR_SDATE IS NULL OR APPR_EDATE IS NULL) GROUP BY TENANT_ID, ENTER_CD, SABUN, YMD) R ON C.TENANT_ID = R.TENANT_ID AND C.ENTER_CD = R.ENTER_CD AND C.SABUN = R.SABUN AND C.YMD = R.YMD WHERE C.YMD BETWEEN #{sYmd} AND #{eYmd} AND C.TENANT_ID = #{tenantId} AND C.ENTER_CD = #{enterCd} AND ((R.PLAN_SDATE IS NOT NULL AND C.ENTRY_SDATE IS NULL) OR (R.PLAN_EDATE IS NOT NULL AND C.ENTRY_EDATE IS NULL))
AND H.ORG_CD IN (NULL,
#{org}
)
ORDER BY entrySdate desc

SELECT CONCAT(DATE_FORMAT(STR_TO_DATE(SDATE, '%Y%m%d'),'%Y.%m.%d'),' ~ ', DATE_FORMAT(STR_TO_DATE(EDATE, '%Y%m%d'),'%Y.%m.%d')) AS title, WORK_TYPE_NM AS caption_rb, CONCAT(SDATE, '@', group_concat(org_cd), '@', WORK_TYPE_CD) AS key2 FROM( SELECT DISTINCT T.WORK_TYPE_CD, T.SDATE, T.EDATE, WORK_TYPE_NM, H.ORG_CD FROM (SELECT CASE WHEN WORK_TYPE_CD LIKE 'SELE%' THEN FLEXIBLE_SDATE ELSE WEEK_SDATE END AS SDATE , CASE WHEN WORK_TYPE_CD LIKE 'SELE%' THEN FLEXIBLE_EDATE ELSE WEEK_EDATE END AS EDATE , SABUN , WORK_TYPE_CD , TENANT_ID , ENTER_CD , F_WTM_GET_CODE_NM(#{tenantId}, #{enterCd},'WORK_TYPE_CD', WORK_TYPE_CD, F_WTM_DATE_FORMAT(NOW(), 'YMD')) AS WORK_TYPE_NM FROM WTM_WORK_TERM_TIME WHERE TENANT_ID=#{tenantId} AND ENTER_CD=#{enterCd} ) T JOIN WTM_EMP_HIS H ON H.TENANT_ID = T.TENANT_ID AND H.ENTER_CD = T.ENTER_CD
AND H.ORG_CD IN (NULL,
#{org}
)
AND H.SABUN = T.SABUN AND H.EYMD = '29991231' AND H.STATUS_CD = 'AA' WHERE #{month} BETWEEN LEFT(T.SDATE, 6) AND LEFT(T.EDATE, 6) AND F_WTM_DATE_FORMAT(NOW(), 'YMD') > T.SDATE) X GROUP BY WORK_TYPE_CD, SDATE, EDATE ORDER BY KEY2 DESC

SELECT CONCAT(T.SABUN,'@',T.SDATE,'@',T.EDATE) AS key2, H.EMP_NM AS title, T.WORK_TYPE_CD as workTypeCd, T.SDATE as sdate, T.EDATE as edate, TIME_FORMAT(SEC_TO_TIME(SUM(T.APPR_WORK_MINUTE)*60), '%H:%i') as caption_rb, TIME_FORMAT(SEC_TO_TIME(SUM(T.APPR_OT_MINUTE)*60), '%H:%i') as status, H.ORG_CD, F_WTM_GET_ORG_NM(T.TENANT_ID, T.ENTER_CD, H.ORG_CD, F_WTM_DATE_FORMAT(now(), 'YMD')) as caption_lb FROM (SELECT CASE WHEN WORK_TYPE_CD LIKE 'SELE%' THEN FLEXIBLE_SDATE ELSE WEEK_SDATE END AS SDATE , CASE WHEN WORK_TYPE_CD LIKE 'SELE%' THEN FLEXIBLE_EDATE ELSE WEEK_EDATE END AS EDATE , SABUN , WORK_TYPE_CD , APPR_WORK_MINUTE , APPR_OT_MINUTE , TENANT_ID , ENTER_CD FROM WTM_WORK_TERM_TIME WHERE TENANT_ID=#{tenantId} AND ENTER_CD=#{enterCd} ) T JOIN WTM_EMP_HIS H ON H.TENANT_ID = T.TENANT_ID AND H.ENTER_CD = T.ENTER_CD
AND H.ORG_CD IN (NULL,
#{org}
)
AND H.SABUN = T.SABUN AND H.EYMD = '29991231' AND H.STATUS_CD = 'AA' WHERE 1=1 AND T.SDATE = #{sdate} AND T.WORK_TYPE_CD = #{workTypeCd} GROUP BY T.TENANT_ID, T.ENTER_CD, T.SABUN, H.EMP_NM, T.WORK_TYPE_CD, T.SDATE, T.EDATE ORDER BY H.ORG_CD
