mapper.select:
SELECT C.TENANT_ID as tenantId , C.ENTER_CD as enterCd , C.SABUN as sabun , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS orgNm , F_WTM_GET_EMP_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS empNm , C.YMD as ymd , F_WTM_GET_FLEXIBLE_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS flexibleNm , C.TIME_CD_MGR_ID as timeCdMgrId , F_WTM_DATE_FORMAT(C.ENTRY_SDATE, 'YMDHI') as entrySdate , C.ENTRY_STYPE_CD as entryStypeCd , F_WTM_DATE_FORMAT(C.ENTRY_EDATE, 'YMDHI') as entryEdate , C.ENTRY_ETYPE_CD as enteryEtypeCd , C.WORK_CLOSE_YN as workCloseYn , C.HOLIDAY_YN as holidayYn , C.NOTE as note FROM WTM_WORK_CALENDAR C JOIN WTM_EMP_HIS H ON C.TENANT_ID = H.TENANT_ID AND C.ENTER_CD = H.ENTER_CD AND C.SABUN = H.SABUN AND C.YMD BETWEEN H.SYMD AND H.EYMD JOIN ( SELECT TENANT_ID , ENTER_CD , SABUN , YMD , MIN(PLAN_SDATE) AS PLAN_SDATE , MAX(PLAN_EDATE) AS PLAN_EDATE FROM WTM_WORK_DAY_RESULT WHERE TENANT_ID = #{tenantId} AND ENTER_CD = #{enterCd} AND TIME_TYPE_CD IN ('BASE', 'OT') AND YMD BETWEEN #{sYmd} AND #{eYmd} AND (APPR_SDATE IS NULL OR APPR_EDATE IS NULL) GROUP BY TENANT_ID, ENTER_CD, SABUN, YMD) R ON C.TENANT_ID = R.TENANT_ID AND C.ENTER_CD = R.ENTER_CD AND C.SABUN = R.SABUN AND C.YMD = R.YMD WHERE C.YMD BETWEEN #{sYmd} AND #{eYmd} AND C.TENANT_ID = #{tenantId} AND C.ENTER_CD = #{enterCd} AND ((R.PLAN_SDATE IS NOT NULL AND C.ENTRY_SDATE IS NULL) OR (R.PLAN_EDATE IS NOT NULL AND C.ENTRY_EDATE IS NULL))
AND H.ORG_CD IN (NULL,
#{org}
)
ORDER BY entrySdate desc
