mapper.select:
SELECT F_WTM_GET_EMP_ORG_NM(B.TENANT_ID, B.ENTER_CD, A.SABUN, A.EYMD) AS orgNm , A.SABUN AS sabun , B.EMP_NM AS empNm , WORK_TYPE_CD AS workTypeCd , A.SYMD AS symd , A.EYMD AS eymd , TRUNCATE(BASE_MINUTE/60,2) AS baseMinute , TRUNCATE(BASE_OT_MINUTE/60,2) AS baseOtMinute , TRUNCATE(WORK_MINUTE/60,2) AS workMinute , TRUNCATE(OT_MINUTE/60,2) AS otMinute , TRUNCATE(OTN_MINUTE/60,2) AS otnMinute , TRUNCATE(HOL_MINUTE/60,2) AS holMinute , TRUNCATE(HOL_OT_MINUTE/60,2) AS holOtMinute , TRUNCATE(A_WORK_MINUTE/60,2) AS aWorkMinute , TRUNCATE(A_OT_MINUTE/60,2) AS aOtMinute , TRUNCATE(A_OTN_MINUTE/60,2) AS aOtnMinute , TRUNCATE(A_HOL_MINUTE/60,2) AS aHolMinute , TRUNCATE(A_HOL_OT_MINUTE/60,2) AS aHolOtMinute , TRUNCATE(A_NONPAY_MINUTE/60,2) AS aNonpayMinute , TRUNCATE(A_PAY_MINUTE/60,2) AS aPayMinute , TRUNCATE(LATE_MINUTE/60,2) AS lateMinute , TRUNCATE(LEAVE_MINUTE/60,2) AS leaveMinute , TRUNCATE(ABSENCE_MINUTE/60,2) AS absenceMinute FROM WTM_WORKTIME_MON_CLOSE A INNER JOIN WTM_WORKTIME_CLOSE C ON A.WORKTIME_CLOSE_ID = C.WORKTIME_CLOSE_ID INNER JOIN WTM_EMP_HIS B ON C.TENANT_ID = B.TENANT_ID AND C.ENTER_CD = B.ENTER_CD AND A.SABUN = B.SABUN AND A.EYMD BETWEEN B.SYMD AND B.EYMD WHERE C.WORKTIME_CLOSE_ID = ${worktimeCloseId}
AND B.SABUN = #{searchKeyword} OR B.EMP_NM LIKE CONCAT(#{searchKeyword}, '%')
ORDER BY A.SABUN, A.SYMD

if.foreach:
UPDATE WTM_WORKTIME_CLOSE
   SET NOTE = #{item.note}  
	 , UPDATE_DATE = NOW()
	 , UPDATE_ID = #{userId}
 WHERE WORKTIME_CLOSE_ID = #{item.worktimeCloseId}

mapper.select:
{ call P_WTM_CREATE_COMP_MON( 
		#{tenantId, jdbcType=BIGINT, javaType=java.lang.Long, mode=IN}
	  , #{enterCd, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN}
	  , #{worktimeCloseId, jdbcType=BIGINT, javaType=java.lang.Long, mode=IN}
	  ) }


        	SELECT F_WTM_NVL(CLOSE_YN, 'N') AS closeYn
			FROM WTM_WORKTIME_CLOSE
		   WHERE WORKTIME_CLOSE_ID = ${worktimeCloseId}
