mapper.select:

SELECT SUM(CASE WHEN CASE X.HOL_EXCEPT_YN WHEN 'Y' THEN X.SUN_YN ELSE X.HOLIDAY_YN END = 'Y' THEN 0 ELSE 1 END) AS WORK_CNT
FROM (
	SELECT D.HOLIDAY_YN, CASE WHEN H.SUN_YN IS NULL THEN 'N' ELSE H.SUN_YN END AS SUN_YN, M.HOL_EXCEPT_YN
	FROM WTM_FLEXIBLE_STD_MGR M
	JOIN WTM_DAY_MGR D
	ON D.SUN_YMD BETWEEN M.USE_SYMD AND M.USE_EYMD
	LEFT OUTER JOIN WTM_HOLIDAY_MGR H
	ON D.SUN_YMD = H.HOLIDAY_YMD
	AND H.TENANT_ID = #{tenantId}
	AND H.ENTER_CD = #{enterCd}
	WHERE M.FLEXIBLE_STD_MGR_ID = #{flexibleStdMgrId}
	  AND D.SUN_YMD BETWEEN #{sYmd} AND #{eYmd} 
	ORDER BY D.SUN_YMD
) X

SELECT * FROM ( SELECT INNER_TABLE.* , ROWNUM AS ROW_SEQ FROM (
SELECT A.APPL_ID , A.APPL_CD , C.APPL_NM , A.APPL_SABUN , F_WTM_GET_EMP_NM(A.TENANT_ID, A.ENTER_CD, A.APPL_SABUN, A.APPL_YMD ) AS EMP_NM , A.APPL_YMD , A.APPL_IN_SABUN , A.APPL_STATUS_CD , F_WTM_GET_CODE_NM(A.TENANT_ID, A.ENTER_CD, 'APPL_STATUS_CD', A.APPL_STATUS_CD, A.APPL_YMD) AS APPL_STATUS_NM , A.ENTER_CD || '@' || A.APPL_CD || '@' || A.APPL_ID || '@' || #{typeCd} || '@' || A.APPL_SABUN || '@WTM' AS APPL_KEY , CASE WHEN A.APPL_CD = 'OT' THEN 'view://W02View' WHEN A.APPL_CD = 'ENTRY_CHG' THEN 'view://W04View' ELSE NULL END AS URL_LINK FROM WTM_APPL A JOIN WTM_APPL_CODE C ON A.TENANT_ID = C.TENANT_ID AND A.ENTER_CD = C.ENTER_CD AND A.APPL_CD = C.APPL_CD LEFT OUTER JOIN (SELECT * FROM WTM_OT_APPL WHERE SABUN = #{empNo}) O ON A.APPL_ID = O.APPL_ID LEFT OUTER JOIN (SELECT * FROM WTM_OT_CAN_APPL WHERE SABUN = #{empNo}) OC ON A.APPL_ID = OC.APPL_ID WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND (A.APPL_SABUN = #{empNo} OR (A.APPL_CD = 'OT' AND O.SABUN = #{empNo}) OR (A.APPL_CD = 'OT_CAN' AND OC.SABUN = #{empNo})) ORDER BY A.APPL_YMD DESC
) INNER_TABLE WHERE TO_NUMBER(ROWNUM) <= ((#{startPage}+1) * #{pageCount}) ) WHERE ROW_SEQ BETWEEN ((#{startPage}*#{pageCount})+1) AND ((#{startPage}+1) * #{pageCount})

SELECT * FROM ( SELECT INNER_TABLE.* , ROWNUM AS ROW_SEQ FROM (
SELECT A.APPL_ID , A.APPL_CD , C.APPL_NM , A.APPL_SABUN , F_WTM_GET_EMP_NM(A.TENANT_ID, A.ENTER_CD, A.APPL_SABUN, A.APPL_YMD ) AS EMP_NM , A.APPL_YMD , A.APPL_IN_SABUN , A.APPL_STATUS_CD , F_WTM_GET_CODE_NM(A.TENANT_ID, A.ENTER_CD, 'APPL_STATUS_CD', A.APPL_STATUS_CD, A.APPL_YMD) AS APPL_STATUS_NM , L.APPR_SEQ , L.APPR_SABUN , L.APPR_DATE , L.APPR_STATUS_CD , L.APPR_TYPE_CD , L.APPR_OPINION , A.ENTER_CD || '@' || A.APPL_CD || '@' || A.APPL_ID || '@' || #{typeCd} || '@' || A.APPL_SABUN || '@WTM' AS APPL_KEY , CASE WHEN A.APPL_CD = 'OT' THEN 'view://W02View' WHEN A.APPL_CD = 'ENTRY_CHG' THEN 'view://W04View' ELSE NULL END AS URL_LINK FROM WTM_APPL A JOIN WTM_APPL_CODE C ON A.TENANT_ID = C.TENANT_ID AND A.ENTER_CD = C.ENTER_CD AND A.APPL_CD = C.APPL_CD JOIN WTM_APPL_LINE L ON L.APPL_ID = A.APPL_ID WHERE A.TENANT_ID = #{tenantId} AND A.ENTER_CD = #{enterCd} AND APPR_SABUN = #{empNo} AND L.APPR_STATUS_CD = '10' AND A.APPL_STATUS_CD IN ('21','31') ORDER BY A.APPL_YMD DESC
) INNER_TABLE WHERE TO_NUMBER(ROWNUM) <= ((#{startPage}+1) * #{pageCount}) ) WHERE ROW_SEQ BETWEEN ((#{startPage}*#{pageCount})+1) AND ((#{startPage}+1) * #{pageCount})

