mapper.select:
SELECT B.TENANT_ID , B.ENTER_CD , C.SABUN , F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, A.YMD) AS ORG_NM , C.EMP_NM , DECODE(A.HOLIDAY_YN, 'Y', '휴일근무', '연장근무') AS APPL_NM , A.YMD , A.OT_SDATE , A.OT_EDATE , A.OT_MINUTE , A.REASON_CD , A.REASON , D.SUBS_SDATE , D.SUBS_EDATE FROM WTM_OT_APPL A INNER JOIN WTM_APPL B ON A.APPL_ID = B.APPL_ID INNER JOIN WTM_EMP_HIS C ON B.TENANT_ID = C.TENANT_ID AND B.ENTER_CD = C.ENTER_CD AND B.APPL_SABUN = C.SABUN AND B.APPL_YMD BETWEEN C.SYMD AND C.EYMD LEFT OUTER JOIN WTM_OT_SUBS_APPL D ON A.OT_APPL_ID = D.OT_APPL_ID WHERE B.TENANT_ID = #{tenantId} AND B.ENTER_CD = #{enterCd} AND A.YMD BETWEEN #{sYmd} AND #{eYmd}
AND (C.SABUN = #{searchKeyword} OR C.EMP_NM LIKE #{searchKeyword} || '%')
AND C.ORG_CD IN (NULL,
#{org}
)
