SELECT A.WORK_CALENDAR_ID , A.TENANT_ID, A.ENTER_CD, A.SABUN , F_WTM_GET_EMP_ORG_NM(#{tenantId}, #{enterCd}, A.SABUN, F_WTM_DATE_FORMAT(SYSDATE,'YMD')) AS ORG_NM, F_WTM_GET_EMP_NM(#{tenantId}, #{enterCd}, A.SABUN, F_WTM_DATE_FORMAT(SYSDATE,'YMD')) AS EMP_NM, A.YMD, A.TIME_CD_MGR_ID , F_WTM_DATE_FORMAT(A.ENTRY_SDATE, 'HI') as ENTRY_SDATE, A.ENTRY_STYPE_CD, F_WTM_DATE_FORMAT(A.ENTRY_EDATE, 'HI') as ENTRY_EDATE, A.ENTRY_ETYPE_CD, A.WORK_CLOSE_YN, A.HOLIDAY_YN, A.NOTE, B.FLEXIBLE_STD_MGR_ID, D.EMP_NM, CASE WHEN A.YMD > F_WTM_DATE_FORMAT(SYSDATE,'YMD') THEN SUM(PLAN_MINUTE) WHEN A.YMD < F_WTM_DATE_FORMAT(SYSDATE,'YMD') THEN SUM(APPR_MINUTE) WHEN (A.YMD = F_WTM_DATE_FORMAT(SYSDATE,'YMD') AND A.ENTRY_EDATE IS NOT NULL) THEN SUM(APPR_MINUTE) ELSE SUM(PLAN_MINUTE) END AS TOT_TIME, E.CORE_SHM, E.CORE_EHM FROM WTM_WORK_CALENDAR A JOIN WTM_FLEXIBLE_EMP B ON B.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=B.ENTER_CD AND A.SABUN=B.SABUN AND A.YMD BETWEEN B.SYMD AND B.EYMD LEFT JOIN WTM_WORK_DAY_RESULT C ON C.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=C.ENTER_CD AND A.SABUN=C.SABUN AND C.YMD = A.YMD JOIN WTM_EMP_HIS D ON D.TENANT_ID=A.TENANT_ID AND A.ENTER_CD=D.ENTER_CD AND A.SABUN=D.SABUN AND F_WTM_DATE_FORMAT(SYSDATE,'YMD') BETWEEN D.SYMD AND D.EYMD JOIN WTM_FLEXIBLE_STD_MGR E ON E.FLEXIBLE_STD_MGR_ID = B.FLEXIBLE_STD_MGR_ID WHERE A.YMD BETWEEN REPLACE(#{sYmd}, '-', '') AND REPLACE(#{eYmd}, '-', '') AND #{tenantId} = A.TENANT_ID AND A.ENTER_CD = #{enterCd}
AND (A.SABUN LIKE #{searchKeyword, jdbcType=VARCHAR} || '%' OR D.EMP_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} || '%')
GROUP BY A.WORK_CALENDAR_ID , A.TENANT_ID, A.ENTER_CD, A.SABUN, A.YMD, A.TIME_CD_MGR_ID , A.ENTRY_SDATE, A.ENTRY_STYPE_CD, A.ENTRY_EDATE, A.ENTRY_ETYPE_CD, A.WORK_CLOSE_YN, A.HOLIDAY_YN, A.NOTE, B.FLEXIBLE_STD_MGR_ID, D.EMP_NM,E.CORE_SHM, E.CORE_EHM ORDER BY YMD DESC, SABUN

SELECT * FROM ( SELECT C.WORK_CALENDAR_ID, C.TENANT_ID, C.ENTER_CD, C.SABUN, F_WTM_GET_EMP_ORG_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS ORG_NM, F_WTM_GET_EMP_NM(C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD) AS EMP_NM, C.YMD, C.TIME_CD_MGR_ID, F_WTM_DATE_FORMAT(C.ENTRY_SDATE, 'HI') as ENTRY_SDATE, C.ENTRY_STYPE_CD, F_WTM_DATE_FORMAT(C.ENTRY_EDATE, 'HI') as ENTRY_SDATE, C.ENTRY_ETYPE_CD, C.WORK_CLOSE_YN, C.HOLIDAY_YNn, C.NOTE, E.FLEXIBLE_STD_MGR_ID, CASE WHEN C.YMD > F_WTM_DATE_FORMAT(SYSDATE,'YMD') THEN SUM(PLAN_MINUTE) WHEN C.YMD < F_WTM_DATE_FORMAT(SYSDATE,'YMD') THEN SUM(APPR_MINUTE) WHEN (C.YMD = F_WTM_DATE_FORMAT(SYSDATE,'YMD') && C.ENTRY_EDATE IS NOT NULL) THEN SUM(APPR_MINUTE) ELSE SUM(PLAN_MINUTE) END AS TOT_TIME FROM WTM_WORK_CALENDAR C JOIN WTM_FLEXIBLE_EMP E ON E.TENANT_ID = C.TENANT_ID AND E.ENTER_CD = C.ENTER_CD AND E.SABUN = C.SABUN AND C.YMD BETWEEN E.SYMD AND E.EYMD LEFT JOIN WTM_WORK_DAY_RESULT R ON C.TENANT_ID = R.TENANT_ID AND C.ENTER_CD = R.ENTER_CD AND C.SABUN=R.SABUN AND C.YMD = R.YMD JOIN WTM_EMP_HIS H ON C.TENANT_ID = H.TENANT_ID AND C.ENTER_CD = H.ENTER_CD AND C.SABUN = H.SABUN AND C.YMD BETWEEN H.SYMD AND H.EYMD WHERE C.TENANT_ID = #{tenantId} AND C.ENTER_CD = #{enterCd} AND C.YMD BETWEEN #{sYmd} AND #{eYmd} AND H.ORG_CD IN (NULL,
#{org}
) GROUP BY C.TENANT_ID, C.ENTER_CD, C.SABUN, C.YMD ) T
WHERE (T.SABUN LIKE #{searchKeyword} || '%' OR T.EMP_NM LIKE '%' || #{searchKeyword} || '%')
ORDER BY YMD DESC, SABUN

{ call P_WTM_UPDATE_TIMECARD( 
	#{tenantId, jdbcType=BIGINT, javaType=java.lang.Long, mode=IN}, 
	#{enterCd, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{sabun, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{ymd, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{inoutType, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{entryType, jdbcType=VARCHAR, javaType=java.lang.String, mode=IN},  
	#{rtnYmd, jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT},
	#{sqlErrm, jdbcType=VARCHAR, javaType=java.lang.String, mode=OUT} 
) }

