mapper.select:
SELECT * FROM ( SELECT EMP_HIS_ID , ENTER_CD , SABUN , EMP_NM , EMP_ENG_NM , SYMD , EYMD , STATUS_CD , ORG_CD , BUSINESS_PLACE_CD , DUTY_CD , POS_CD , CLASS_CD , JOB_GROUP_CD , JOB_CD , PAY_TYPE_CD , LEADER_YN , NOTE , F_WTM_GET_ORG_NM(TENANT_ID, ENTER_CD, ORG_CD, SYMD) AS ORG_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'DUTY_CD', DUTY_CD, SYMD) AS DUTY_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'POS_CD', POS_CD, SYMD) AS POS_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'CLASS_CD', CLASS_CD, SYMD) AS CLASS_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'JOB_CD', JOB_CD, SYMD) AS JOB_NM , EMP_ID FROM WTM_EMP_HIS WHERE TENANT_ID = #{tenantId} AND ENTER_CD = #{enterCd} AND CASE WHEN length(#{sYmd, jdbcType=VARCHAR}) > 0 THEN REPLACE(#{sYmd, jdbcType=VARCHAR}, '-', '') ELSE F_WTM_DATE_FORMAT(SYSDATE,'YMD') END BETWEEN SYMD AND EYMD AND STATUS_CD <> 'RA'
AND ORG_CD IN (NULL,
#{org}
)
) T WHERE 1=1 AND (SABUN LIKE #{searchKeyword, jdbcType=VARCHAR} || '%' OR EMP_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%' OR ORG_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%' OR DUTY_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%' OR POS_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%' OR CLASS_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%' OR JOB_NM LIKE '%' || #{searchKeyword, jdbcType=VARCHAR} ||'%')

SELECT EMP_HIS_ID , ENTER_CD , SABUN , EMP_NM , EMP_ENG_NM , SYMD , EYMD , STATUS_CD , ORG_CD , BUSINESS_PLACE_CD , DUTY_CD , POS_CD , CLASS_CD , JOB_GROUP_CD , JOB_CD , PAY_TYPE_CD , LEADER_YN , NOTE , F_WTM_GET_ORG_NM(TENANT_ID, ENTER_CD, ORG_CD, SYMD) AS ORG_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'DUTY_CD', DUTY_CD, SYMD) AS DUTY_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'POS_CD', POS_CD, SYMD) AS POS_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'CLASS_CD', CLASS_CD, SYMD) AS CLASS_NM , F_WTM_GET_CODE_NM(TENANT_ID, ENTER_CD, 'JOB_CD', JOB_CD, SYMD) AS JOB_NM , EMP_ID FROM WTM_EMP_HIS WHERE EMP_HIS_ID = #{empHisId} AND STATUS_CD <> 'RA'
AND ORG_CD IN (NULL,
#{org}
)

mapper.update:
UPDATE comm_user U SET PASSWORD = ( SELECT PASSWORD FROM ( SELECT NULL AS TENANT_ID , NULL AS ENTER_CD , NULL AS SABUN , NULL AS PASSWORD FROM DUAL
UNION SELECT ${tenantId} AS TENANT_ID , F_WTM_AES_ENCRYPT(#{enterCd}, #{enterCd}) AS ENTER_CD , F_WTM_AES_ENCRYPT( #{m.sabun}, #{enterCd}) AS SABUN , #{m.password} AS PASSWORD FROM DUAL
) T WHERE T.TENANT_ID = U.tenant_id AND T.ENTER_CD = U.enter_cd AND T.SABUN = U.login_id )

DELETE FROM comm_user WHERE (tenant_id, enter_cd, login_id) IN ( SELECT TENANT_ID , F_WTM_AES_ENCRYPT(ENTER_CD, ENTER_CD) , F_WTM_AES_ENCRYPT(SABUN, ENTER_CD) FROM WTM_EMP_HIS WHERE EMP_HIS_ID IN (NULL
#{empHisId}
) )
