mapper.select:

SELECT A.YMD as key2, RIGHT(A.YMD, 2) as title, DATE_FORMAT(A.ENTRY_SDATE, '%H:%i') as caption_lb, DATE_FORMAT(A.ENTRY_EDATE, '%H:%i') as caption_rb, T.TIME_NM as status 
FROM WTM_WORK_CALENDAR A 
	JOIN WTM_TIME_CD_MGR T
 		ON T.TIME_CD_MGR_ID = A.TIME_CD_MGR_ID	
WHERE A.TENANT_ID = #{tenantId}
	AND A.ENTER_CD = #{enterCd}
	AND A.SABUN = #{sabun}
	AND DATE_FORMAT(A.YMD,'%Y%c') = #{month}
	ORDER BY A.YMD DESC


SELECT CONCAT_WS('@',DATE_FORMAT(A.INOUT_DATE, "%Y%c%d%H%i%s"),A.INOUT_TYPE_CD) AS key2,  
	F_WTM_GET_CODE_NM(#{tenantId},#{enterCd}, 'INOUT_TYPE_CD', A.INOUT_TYPE_CD, #{ymd}) AS title,
 	DATE_FORMAT(A.INOUT_DATE, '%H:%i') as caption_lb,
 	F_WTM_GET_CODE_NM(#{tenantId},#{enterCd}, 'ENTRY_TYPE_CD', A.ENTRY_TYPE_CD, #{ymd}) AS caption_rb
FROM WTM_INOUT_HIS A
WHERE A.TENANT_ID = #{tenantId}
	AND A.ENTER_CD = #{enterCd}
	AND A.SABUN = #{sabun}
	AND DATE_FORMAT(A.YMD,'%Y%c%d') = #{ymd}
	ORDER BY A.INOUT_DATE DESC


SELECT DATE_FORMAT(INOUT_DATE, '%Y-%c-%d') as ymd,
	F_WTM_GET_CODE_NM(#{tenantId},#{enterCd},'INOUT_TYPE_CD', INOUT_TYPE_CD, DATE_FORMAT(INOUT_DATE, '%Y%c%d')) AS inoutTypeCd,
	DATE_FORMAT(INOUT_DATE, '%H:%i:%s') as inoutDate,
	DATE_FORMAT(INOUT_DATE, "%Y%c%d%H%i%s") AS id
FROM WTM_INOUT_HIS 
WHERE TENANT_ID = #{tenantId}
	AND ENTER_CD = #{enterCd}
	AND SABUN = #{sabun}
	AND INOUT_TYPE_CD = #{inoutTypeCd}
	AND DATE_FORMAT(#{inoutDate}, '%Y%c%d%H%i%s') = DATE_FORMAT(INOUT_DATE, '%Y%c%d%H%i%s');
