mapper.select:

        	SELECT F_WTM_DATE_FORMAT(MAX(UPDATE_DATE), 'YMDHIS') AS LAST_DATE
			  FROM WTM_IF_HIS
			 WHERE TENANT_ID = #{tenantId}
			   AND IF_ITEM = #{ifType}
			   AND IF_STATUS = 'OK'

mapper.insert:
INSERT INTO WTM_IF_HIS
(
	  TENANT_ID
	, IF_STATUS
	, IF_ITEM 
	, IF_SEARCH_DATE 
	, IF_MSG 
	, UPDATE_DATE
) VALUES (
	  #{tenantId}
	, #{ifStatus}
	, #{ifItem}
	, F_WTM_TO_DATE(#{ifEndDate}, 'YMDHIS')
	, #{ifMsg}
	, F_WTM_TO_DATE(#{updateDate}, 'YMDHIS')
)

mapper.select:

