			, CODE_NM 
			, SYMD 
			, EYMD 
			, NOTE
			, UPDATE_DATE
			, UPDATE_ID
		) VALUES 
		(     #{item.tenantId}
        	, #{item.enterCd}
        	, #{item.grpCodeCd}
        	, #{item.codeCd}
        	, #{item.codeNm}
        	, #{item.symd}
        	, #{item.eymd}
        	, #{item.note}
        	, SYSDATE
        	, '0'
        )

mapper.update:
UPDATE WTM_CODE SET NOTE = CASE
when CODE_ID = #{item.codeId} then #{item.note}
END , UPDATE_DATE = SYSDATE , UPDATE_ID = '0' WHERE CODE_ID IN
#{item.codeId}


UPDATE WTM_CODE
   SET EYMD =F_WTM_NVL(F_WTM_DATE_FORMAT(DATE_ADD(F_WTM_TO_DATE(#{symd}, 'YMD'), INTERVAL -1 DAY), 'YMD'), '29991231')
  , UPDATE_DATE = SYSDATE
  , UPDATE_ID = '0'
WHERE 1=1
  AND TENANT_ID = #{tenantId}
  AND ENTER_CD = #{enterCd}
  AND GRP_CODE_CD = #{grpCodeCd}
  AND CODE_CD = #{codeCd}
  AND #{symd} > SYMD


UPDATE WTM_CODE
   SET SYMD =F_WTM_NVL(F_WTM_DATE_FORMAT(DATE_ADD(F_WTM_TO_DATE(#{eymd}, 'YMD'), INTERVAL -1 DAY), 'YMD'), '29991231')
  , UPDATE_DATE = SYSDATE
  , UPDATE_ID = '0'
WHERE 1=1
  AND TENANT_ID = #{tenantId}
  AND ENTER_CD = #{enterCd}
  AND GRP_CODE_CD = #{grpCodeCd}
  AND CODE_CD = #{codeCd}
  AND #{symd} < SYMD

MERGE INTO WTM_HOLIDAY_MGR M USING ( SELECT T.TENANT_ID , T.ENTER_CD , T.BISINESS_PLACE_CD , T.HOLIDAY_YMD , T.HOLIDAY_NM , T.SUN_YN , T.FESTIVE_YN , T.PAY_YN , SYSDATE AS UPDATE_DATE , '0' AS UPDATE_ID FROM ( SELECT NULL AS TENANT_ID , NULL AS ENTER_CD , NULL AS BISINESS_PLACE_CD , NULL AS HOLIDAY_YMD , NULL AS HOLIDAY_NM , NULL AS SUN_YN , NULL AS FESTIVE_YN , NULL AS PAY_YN FROM DUAL
UNION ALL SELECT TRIM(#{item.tenantId}) AS TENANT_ID , TRIM(#{item.enterCd}) AS ENTER_CD , TRIM(#{item.locationCd}) AS BISINESS_PLACE_CD , TRIM(#{item.holidayYmd}) AS HOLIDAY_YMD , TRIM(#{item.holidayNm}) AS HOLIDAY_NM , TRIM(#{item.sunYn}) AS SUN_YN , TRIM(#{item.festiveYn}) AS FESTIVE_YN , TRIM(#{item.payYn}) AS PAY_YN FROM DUAL
) T WHERE T.TENANT_ID IS NOT NULL AND T.ENTER_CD IS NOT NULL AND T.BISINESS_PLACE_CD IS NOT NULL AND T.HOLIDAY_YMD IS NOT NULL ) S ON (M.TENANT_ID = S.TENANT_ID AND M.ENTER_CD = S.ENTER_CD AND M.BISINESS_PLACE_CD = S.BISINESS_PLACE_CD AND M.HOLIDAY_YMD = S.HOLIDAY_YMD) WHEN MATCHED THEN UPDATE SET M.HOLIDAY_NM = S.HOLIDAY_NM , SUN_YN = S.SUN_YN , FESTIVE_YN = S.FESTIVE_YN , PAY_YN = S.PAY_YN , UPDATE_DATE = S.UPDATE_DATE , UPDATE_ID = S.UPDATE_ID WHEN NOT MATCHED THEN INSERT ( TENANT_ID , ENTER_CD , BISINESS_PLACE_CD , HOLIDAY_YMD , HOLIDAY_NM , SUN_YN , FESTIVE_YN , PAY_YN , UPDATE_DATE , UPDATE_ID ) VALUES ( S.TENANT_ID , S.ENTER_CD , S.BISINESS_PLACE_CD , S.HOLIDAY_YMD , S.HOLIDAY_NM , S.SUN_YN , S.FESTIVE_YN , S.PAY_YN , S.UPDATE_DATE , S.UPDATE_ID )

