        when TAA_CODE_ID = #{item.taaCodeId} then #{item.taaNm}
     END
  , TAA_TYPE_CD = CASE 

        when TAA_CODE_ID = #{item.taaCodeId} then #{item.taaTypeCd}
     END
  , HOL_INCL_YN = CASE 

        when TAA_CODE_ID = #{item.taaCodeId} then #{item.holInclYn}
     END
  , REQUEST_TYPE_CD = CASE 

        when TAA_CODE_ID = #{item.taaCodeId} then #{item.requestTypeCd}
     END
  , WORK_YN = CASE 

        when TAA_CODE_ID = #{item.taaCodeId} then #{item.workYn}
     END
  , UPDATE_DATE = SYSDATE
  , UPDATE_ID = '0'
WHERE TAA_CODE_ID IN 

	#{item.taaCodeId}

MERGE INTO WTM_ORG_CODE C USING ( SELECT T.TENANT_ID , T.ENTER_CD , T.ORG_CD , T.SYMD , T.ORG_NM , T.EYMD , T.ORG_TYPE , T.NOTE , SYSDATE AS UPDATE_DATE , '0' AS UPDATE_ID FROM ( SELECT NULL AS TENANT_ID , NULL AS ENTER_CD , NULL AS ORG_CD , NULL AS SYMD , NULL AS ORG_NM , NULL AS EYMD , NULL AS ORG_TYPE , NULL AS NOTE FROM DUAL
UNION ALL SELECT TRIM(#{item.tenantId}) AS TENANT_ID , TRIM(#{item.enterCd}) AS ENTER_CD , TRIM(#{item.orgCd}) AS ORG_CD , TRIM(#{item.symd}) AS SYMD , TRIM(#{item.orgNm}) AS ORG_NM , TRIM(#{item.eymd}) AS EYMD , TRIM(#{item.orgType}) AS ORG_TYPE , TRIM(#{item.note}) AS NOTE FROM DUAL
) T WHERE T.TENANT_ID IS NOT NULL AND T.ENTER_CD IS NOT NULL AND T.ORG_CD IS NOT NULL AND T.SYMD IS NOT NULL ) S ON (C.TENANT_ID = S.TENANT_ID AND C.ENTER_CD = S.ENTER_CD AND C.ORG_CD = S.ORG_CD AND C.SYMD = S.SYMD) WHEN MATCHED THEN UPDATE SET ORG_NM = S.ORG_NM , ORG_TYPE = S.ORG_TYPE , EYMD = S.EYMD , NOTE = S.NOTE , UPDATE_DATE = S.UPDATE_DATE , UPDATE_ID = S.UPDATE_ID WHEN NOT MATCHED THEN INSERT ( TENANT_ID , ENTER_CD , ORG_CD , SYMD , ORG_NM , EYMD , ORG_TYPE , NOTE , UPDATE_DATE , UPDATE_ID ) VALUES ( S.TENANT_ID , S.ENTER_CD , S.ORG_CD , S.SYMD , S.ORG_NM , S.EYMD , S.ORG_TYPE , S.NOTE , S.UPDATE_DATE , S.UPDATE_ID )


