MERGE INTO WTM_ORG_CHART_DET D USING ( SELECT T.ORG_CHART_ID , T.ORG_CD , T.PRIOR_ORG_CD , T.SEQ , T.ORG_LEVEL , T.ORDER_SEQ , SYSDATE AS UPDATE_DATE , '0' AS UPDATE_ID FROM ( SELECT NULL AS ORG_CHART_ID , NULL AS ORG_CD , NULL AS PRIOR_ORG_CD , NULL AS SEQ , NULL AS ORG_LEVEL , NULL AS ORDER_SEQ FROM DUAL
UNION ALL SELECT TRIM(#{item.orgChartId}) AS ORG_CHART_ID , TRIM(#{item.orgCd}) AS ORG_CD , TRIM(#{item.priorOrgCd}) AS PRIOR_ORG_CD , TRIM(#{item.seq}) AS SEQ , TRIM(#{item.orgLevel}) AS ORG_LEVEL , TRIM(#{item.orderSeq}) AS ORDER_SEQ FROM DUAL
) T WHERE T.ORG_CHART_ID IS NOT NULL AND T.ORG_CD IS NOT NULL ) S ON (D.ORG_CHART_ID = S.ORG_CHART_ID AND D.ORG_CD = S.ORG_CD) WHEN MATCHED THEN UPDATE SET PRIOR_ORG_CD = S.PRIOR_ORG_CD , SEQ = S.SEQ , ORG_LEVEL = S.ORG_LEVEL , ORDER_SEQ = S.ORDER_SEQ , UPDATE_DATE = S.UPDATE_DATE , UPDATE_ID = S.UPDATE_ID WHEN NOT MATCHED THEN INSERT ( ORG_CHART_ID , ORG_CD , PRIOR_ORG_CD , SEQ , ORG_LEVEL , ORDER_SEQ , UPDATE_DATE , UPDATE_ID ) VALUES ( S.ORG_CHART_ID , S.ORG_CD , S.PRIOR_ORG_CD , S.SEQ , S.ORG_LEVEL , S.ORDER_SEQ , S.UPDATE_DATE , S.UPDATE_ID )

