mapper.insert:
        INSERT INTO WTM_FLEXIBLE_APPLY_GROUP (
        	  FLEXIBLE_APPLY_ID
        	, ORG_CD
        	, DUTY_CD
        	, POS_CD
        	, CLASS_CD
        	, WORKTEAM_CD
        	, NOTE
        	, UPDATE_DATE
        	, UPDATE_ID
        ) VALUES 

		(     #{item.flexibleApplyId}
        	, #{item.orgCd}
        	, #{item.dutyCd}
        	, #{item.posCd}
        	, #{item.classCd}
        	, #{item.workteamCd}
        	, #{item.note}
        	, NOW()
        	, #{item.userId}
        )

        ON DUPLICATE KEY UPDATE
              ORG_CD = VALUES(ORG_CD)
            , DUTY_CD = VALUES(DUTY_CD)
            , POS_CD = VALUES(POS_CD)
            , CLASS_CD = VALUES(CLASS_CD)
            , WORKTEAM_CD = VALUES(WORKTEAM_CD)
            , NOTE = VALUES(NOTE)
            , UPDATE_DATE = NOW()

        INSERT INTO WTM_FLEXIBLE_APPLY_EMP (
        	  FLEXIBLE_APPLY_ID
        	, SABUN
        	, NOTE
        	, UPDATE_DATE
        	, UPDATE_ID
        ) VALUES 

		(     #{item.flexibleApplyId}
        	, #{item.sabun}
        	, #{item.note}
        	, NOW()
        	, #{item.userId}
        )

        ON DUPLICATE KEY UPDATE
              SABUN = VALUES(SABUN)
            , NOTE = VALUES(NOTE)
            , UPDATE_DATE = NOW()
            , UPDATE_ID = #{userId}
