mapper.select:

SELECT FLEXIBLE_STD_MGR_ID as code
     , FLEXIBLE_NM as codeNm
  FROM WTM_FLEXIBLE_STD_MGR 
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
 ORDER BY FLEXIBLE_STD_MGR_ID


SELECT FLEXIBLE_STD_MGR_ID as code
     , FLEXIBLE_NM as codeNm
  FROM WTM_FLEXIBLE_STD_MGR 
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND WORK_TYPE_CD = #{workTypeCd}
 ORDER BY FLEXIBLE_STD_MGR_ID
