mapper.select:

        	SELECT DATE_FORMAT(MAX(UPDATE_DATE), '%Y%m%d%H%i%s') AS LAST_DATE
			  FROM WTM_IF_HIS
			 WHERE TENANT_ID = #{tenantId}
			   AND IF_ITEM = #{ifType}
			   AND IF_STATUS = 'OK'

mapper.insert:
INSERT INTO WTM_IF_HIS
(
	  TENANT_ID
	, IF_STATUS
	, IF_ITEM 
	, IF_SEARCH_DATE 
	, IF_MSG 
	, UPDATE_DATE
) VALUES (
	  #{tenantId}
	, #{ifStatus}
	, #{ifItem}
	, F_WTM_TO_DATE(#{ifEndDate}, '%Y%m%d%H%i%s')
	, #{ifMsg}
	, F_WTM_TO_DATE(#{updateDate}, '%Y%m%d%H%i%s')
)

mapper.select:

SELECT CODE_ID
  FROM WTM_CODE
 WHERE TENANT_ID = #{tenantId}
   AND ENTER_CD = #{enterCd}
   AND GRP_CODE_CD = #{grpCodeCd}
   AND CODE_CD = #{codeCd}
   AND CODE_NM = #{codeNm}
   AND SYMD = #{symd}

mapper.insert:
		INSERT INTO WTM_CODE
		(
			  TENANT_ID
			, ENTER_CD 
			, GRP_CODE_CD 
			, CODE_CD 
			, CODE_NM 
			, SYMD 
			, EYMD 
			, NOTE
			, UPDATE_DATE
			, UPDATE_ID
		) VALUES 

		(     #{item.tenantId}
        	, #{item.enterCd}
        	, #{item.grpCodeCd}
        	, #{item.codeCd}
        	, #{item.codeNm}
        	, #{item.symd}
        	, #{item.eymd}
        	, #{item.note}
        	, NOW()
        	, '0'
        )

mapper.update:
UPDATE WTM_CODE SET NOTE = CASE
when CODE_ID = #{item.codeId} then #{item.note}
END , UPDATE_DATE = NOW() , UPDATE_ID = '0' WHERE CODE_ID IN
#{item.codeId}


UPDATE WTM_CODE
   SET EYMD =F_WTM_NVL(F_WTM_DATE_FORMAT(DATE_ADD(F_WTM_TO_DATE(#{symd}, '%Y%m%d'), INTERVAL -1 DAY), '%Y%m%d'), '29991231')
  , UPDATE_DATE = NOW()
  , UPDATE_ID = '0'
WHERE 1=1
  AND TENANT_ID = #{tenantId}
  AND ENTER_CD = #{enterCd}
  AND GRP_CODE_CD = #{grpCodeCd}
  AND CODE_CD = #{codeCd}
  AND #{symd} > SYMD


UPDATE WTM_CODE
   SET SYMD =F_WTM_NVL(F_WTM_DATE_FORMAT(DATE_ADD(F_WTM_TO_DATE(#{eymd}, '%Y%m%d'), INTERVAL -1 DAY), '%Y%m%d'), '29991231')
  , UPDATE_DATE = NOW()
  , UPDATE_ID = '0'
WHERE 1=1
  AND TENANT_ID = #{tenantId}
  AND ENTER_CD = #{enterCd}
  AND GRP_CODE_CD = #{grpCodeCd}
  AND CODE_CD = #{codeCd}
  AND #{symd} < SYMD

